import React from 'react'
import {Link} from 'react-router-dom'
import {AppBar, Avatar, Typography, Toolbar, Button} from '@material-ui/core';
import useStyles from './styles'
import memories from '../../images/memories.png';
import { useDispatch } from 'react-redux';

const Navbar = () => {
    const classes = useStyles();
    
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const history = useHistory();
    const location = useLocation();

    const logout = () => {
        dispatch({type:'LOGOUT'});
        history.push('/');
        setUser(null);
    };

    useEffect(() => {
        const token = user?.token;

        //JWT karni parti but

        setUser(JSON.parse(localStorage.getItem('profile')));
    }, [location]);
    
    return (
    
    
      <AppBar className={classes.appBar} position="static" color="inherit">
        <div className={classes.brandContainer}>
            <Typography component={Link} to="/" className={classes.heading} variant="h2" align="center">BankaiTech</Typography>
            <img className={classes.image} src={memories} alt="icon" height="60" />
        </div>
        <Toolbar className={classes.toolbar}>
            { user ? (
                <div className={classes.profile}>
                    <Avatar className={classes.purple} alt={user.result.name} src={user.result.imageUrl}>{user.result.name.charAt(0)}</Avatar>
                    <Typography className = {classes.userName} variant="h6">{user.result.name}</Typography>
                    <Button variant="contained" className={classes.logout} color="secondary">Logout</Button>
                </div>
            ) : (
                <Button component={Link} to="/Auth" variant="contained" className={classes.logout} color="primary">Sign In</Button>
            )}
        </Toolbar>
        
      </AppBar>
    
  );
}

export default Navbar
